        return {INF, 0};
    if (l <= b && e <= r) {
      //  cout << "              " << seg[id].Y << endl;
        return seg[id];
    }
    int mid = b + e >> 1;
    return Merge(get(l, r, b, mid, id << 1), get(l, r, mid, e, id << 1 | 1));
}
