  st[t][v] = infll;
  if (tl == tr - 1) {
    return;
  }
  int tm = (tl + tr) / 2;
  buildst(t, 2 * v, tl, tm);
  buildst(t, 2 * v + 1, tm, tr);
}

void update(int t, int v, int tl, int tr, int l, int r, long long val) {
  if (l == tl && r == tr) {
    st[t][v] = min(st[t][v], val);
    return;
  }
  int tm = (tl + tr) / 2;
  if (l < tm) {
    update(t, 2 * v, tl, tm, l, min(r, tm), val);
  }
  if (r > tm) {
    update(t, 2 * v + 1, tm, tr, max(l, tm), r, val);
  }
}

long long get(int t, int v, int tl, int tr, int pos) {
  long long res = st[t][v];
  if (tl == tr - 1) {
    return res;
  }
  int tm = (tl + tr) / 2;
  if (pos < tm) {
    res = min(res, get(t, 2 * v, tl, tm, pos));
  } else {
    res = min(res, get(t, 2 * v + 1, tm, tr, pos));
  }
  return res;
}

void solve() {
  buildst(0, 1, 0, n + 1);
  buildst(1, 1, 0, n + 1);
  {
    int l = 0, r = n;
    while (l < r - 1) {
      int m = (l + r) / 2;
      if (2 * (x[m] - x[0]) >= t) {
        r = m;
      } else {
        l = m;
      }
    }
    int pos = r;
    if (pos < n) {
      update(0, 1, 0, n, pos, n, -2 * x[0]);
    }
    if (0 < pos) {
      update(1, 1, 0, n, 0, pos, t);
    }
  }
  for (int i = 0; i < n; i++) {
    dp[i] = min(get(0, 1, 0, n, i) + 3ll * x[i], get(1, 1, 0, n, i) + x[i]);
    if (i + 1 == n) {
      break;
    }
    int pos;
    {
      int l = i + 1, r = n;
      while (l < r - 1) {
        int m = (l + r) / 2;
        if (2 * (x[m] - x[i + 1]) >= t) {
          r = m;
        } else {
          l = m;
        }
      }
      pos = r;
    }
    if (pos < n) {
      update(0, 1, 0, n, pos, n, dp[i] - 2ll * x[i + 1] - x[i]);
    }
    if (i + 1 < pos) {
      update(1, 1, 0, n, i + 1, pos, dp[i] + t - x[i]);
    }
  }
  long long res = dp[n - 1] + e - x[n - 1];
  printf("%lld\n", res);
}

