struct LazySegmentTree
{
    //A: data, B: ä½œç”¨ç´ 
    using T_A = int;
    using T_B = int;
    T_A id_A = INF;
    T_B id_B = INF;
    int N;
    vector<T_A> dat;
    vector<T_B> ope;

    T_A F_AA(T_A u, T_A v){
        return min(u, v);
    }

    T_A F_AB(T_A u, T_B v, int sz){
        return min(u, v);
    }

    T_B F_BB(T_B u, T_B v){
        return min(u, v);
    }

    LazySegmentTree(int n){
        N = 1;
        while(n > N) N = N << 1;
        dat = vector<T_A>(2 * N - 1, id_A);
        ope = vector<T_B>(2 * N - 1, id_B);
    }

    LazySegmentTree(int n, vector<T_A> v){
        N = 1;
        while(n > N) N = N << 1;
        dat = vector<T_A>(2 * N - 1, id_A);
        ope = vector<T_B>(2 * N - 1, id_B);
        REP(i, n) dat[i + N - 1] = v[i];
        IREP(i, N - 1) dat[i] = F_AA(dat[i * 2 + 1], dat[i * 2 + 2]); 
    }

    //é…å»¶è©•ä¾¡
    void eval(int k, int l, int r){
        if(ope[k] != id_B){
            dat[k] = F_AB(dat[k], ope[k], r - l);
            if(r - l > 1){
                ope[k * 2 + 1] = F_BB(ope[k * 2 + 1], ope[k]);
                ope[k * 2 + 2] = F_BB(ope[k * 2 + 2], ope[k]);
            }
            ope[k] = id_B;
        }
    }

    void update(int a, int b, T_B x, int k, int l, int r){

        eval(k, l, r);
        if(b <= l || r <= a) return;

        if(a <= l && r <= b){
            ope[k] = F_BB(ope[k], x);
            eval(k, l, r);
        }else{
            update(a, b, x, k * 2 + 1, l, (l + r) / 2);
            update(a, b, x, k * 2 + 2, (l + r) / 2, r);
            dat[k] = F_AA(dat[k * 2 + 1], dat[k * 2 + 2]);
        }

    }

    T_A get(int a, int b, int k, int l, int r){
        if(r <= a || b <= l) return id_A;

        eval(k, l, r);
        if(a <= l && r <= b) return dat[k];
        else{
            T_A vl = get(a, b, k * 2 + 1, l, (l + r) / 2);
            T_A vr = get(a, b, k * 2 + 2, (l + r) / 2, r);
            return F_AA(vl, vr);
        }
    }

    //[a,b)ã®åŒºé–“æ›´æ–°
    void update(int a, int b, T_B x){ update(a, b, x, 0, 0, N); }

    //[a, b)ã®æ¼”ç®—çµæžœ
    T_A get(int a, int b){ return get(a, b, 0, 0, N); }

    T_A val(int k){ return get(k, k + 1); }
};

signed main(){

    int N, E, T; cin >> N >> E >> T;
    vec x(N); cin >> x;

    LazySegmentTree ST1(N), ST2(N);
    int t0 = 0;
    REP(i, N){
        int t;
        if(i == 0) t = t0 + x[i];
        else t = t0 + x[i] - x[i - 1];

        int i0 = Upper_bound(x, x[i] + T / 2);
        ST1.update(i, i0, t + T - x[i]);
        ST2.update(i0, N, t - 3 * x[i]);

        int v1 = ST1.val(i) + x[i];
        int v2 = ST2.val(i) + 3 * x[i];
        t0 = min(v1, v2);
    }
    int ans = t0 + E - x[N - 1];
    Out(ans);

    return 0;
}

