long long calc(int a, int b)
{
	return max(f[b - 1] + t, f[b - 1] + 2 * x[a] - 2 * x[b]);
}

priority_queue<long long> q;

int main()
{
	scanf("%d%d%d", &n, &e, &t);
	for(int i = 1; i <= n; ++i)
		scanf("%d", &x[i]);
	f[1] = t;
	int now = 0;
	for(int i = 2; i <= n; ++i)
	{
		f[i] = f[i - 1] + t;
		int l = 1;
		int r = i - 1, mid, key = i;
		while(l <= r)
		{
			mid = l + r;
			mid>>=1;
			if(t + 2 * x[mid] - 2 * x[i] >= 0)
			{
				r = mid - 1;
				key = mid;
			}
			else
				l = mid + 1;
		}
		f[i] = min(f[i], max(f[key - 1] + 2 * x[i] - 2 * x[key], f[key - 1] + t));
		if(key > 1)
		{
			while(now < key - 1)
			{
				++now;
				q.push(2 * x[now] - f[now - 1]);
			}
			f[i] = min(f[i], 2 * x[i] - q.top());
		}
	}

//	printf("%d\n", f[n] + e);
	cout<<f[n] + e<<endl;

	return 0;
}

