const int inf = 1e9 + 9;
const ll mod = 1e9 + 7;
const double eps = 1e-8;
const double pi = acos(-1);

int n;
ll e, t;
ll x[100010];

ll memo[100010];

ll calc(int k) {
    if (memo[k] < 0) {
        if (k == n) memo[k] = 0;
        else {
            // wait
            ll res = t + x[k+1] - x[k] + calc(k+1);
            int x0 = k+1, x3 = n-1, x1=k+1, x2=k+2;
            ll v1, v2;
            ll d;

            while (x3 - x0 > 2) {
                x1 = x0 + (x3-x0)/3;
                x2 = x0 + 2*(x3-x0)/3;
                d = x[x1] - x[x1];
                v1 = 3*d + max(0LL, t-2*d) + calc(x1+1)+x[x1+1]-x[x1];
                d = x[x2] - x[x2];
                v2 = 3*d + max(0LL, t-2*d) + calc(x2+1)+x[x2+1]-x[x2];
                if (v1 < v2) {
                    x3 = x2;
                } else {
                    x0 = x1;
                }
            }


            for (int i = x0; i <= x3; i++) {
                ll d = x[i] - x[k];
                res = min(res, 3*d + max(0LL, t-2*d) + calc(i+1)+x[i+1]-x[i]);
            }

            memo[k] = res;
        }
    }
    return memo[k];
}

ll solve() {
    memset(memo, -1, sizeof(memo));
    x[n] = e;
    return calc(0) + x[0];
}

void input() {
    cin >> n >> e >> t;
    for (int i = 0; i < n; i++) cin >> x[i];
}

