    int idx = 0;
    ll sum = 0;
    ll total, tmp;
    multiset<ll> S;
    S.insert(T);
    ll now = 1E18;
    multiset<ll>::iterator it;
    for (int i = 1;i <= N;i++) {
    	now += 3 * (X[i] - X[i - 1]);
