    		// Eject X[idx].
    		if (idx != 0) total = T + X[i] - X[idx - 1] + Y[idx - 1];
    		else total = T + X[i];
    		total -= sum;
    		it = S.find(total);
    		S.erase(it);
    		if (idx > 0) tmp = (X[i] - X[idx]) * 3 + Y[idx - 1] + X[idx] - X[idx - 1];
    		else tmp = (X[i] - X[idx]) * 3 + X[1] - X[0];
    		now = min(now, tmp);
    		idx++;
    	}
        S.insert(T + Y[i - 1] + X[i] - X[i - 1] - sum);
