using namespace std;

struct SegTree{
	llint size;
	vector<llint> seg;

	SegTree(){}
	SegTree(llint size){
		this->size = size;
		seg.resize(1<<(size+1));
	}

	void init()
	{
		for(int i = 0; i < (1<<(size+1)); i++) seg[i] = inf;
	}

	void update(llint i, llint val)
	{
		i += (1 << size);
		seg[i] = val;
		while(i > 1){
			i /= 2;
			seg[i] = min(seg[i*2], seg[i*2+1]);
		}
	}

	llint query(int a, int b, int k, int l, int r)
	{
		if(b < l || r < a) return inf;
		if(a <= l && r <= b) return seg[k];
		llint lval = query(a, b, k*2, l, (l+r)/2);
		llint rval = query(a, b, k*2+1, (l+r)/2+1, r);
		return min(lval, rval);
	}
	llint query(int a, int b)
	{
		if(a > b) return inf;
		return query(a, b, 1, 0, (1<<size)-1);
	}
};

llint N, E, T;
llint x[100005];
SegTree seg(17), seg2(17);

int main(void)
{
	cin >> N >> E >> T;
	for(int i = 1; i <= N; i++) cin >> x[i];

	seg.init(), seg.update(0, 0);
	seg2.init(), seg2.update(0, -2*x[1]);

	for(int i = 1; i <= N; i++){
		llint lb = lower_bound(x, x+N, x[i] - T/2) - x;
		llint tmp = seg.query(lb-1, i-1) + T;
		tmp = min(tmp, seg2.query(0, lb-2) + 2*x[i]);
		seg.update(i, tmp), seg2.update(i, tmp - 2*x[i+1]);
	}
	cout << seg.query(N, N) + E << endl;
	return 0;
}

