int main() {
    int n;
    long long T, E;
    cin >> n >> E >> T;
    vector<long long> x(n), d(n), ds(n + 1);
    for (int i = 0; i < n; i++) scanf("%lld", &x[i]);
    for (int i = 0; i < n - 1; i++) d[i] = x[i + 1] - x[i];
    for (int i = 0; i < n; i++) ds[i + 1] = ds[i] + d[i];

    fill_n(dp, 101010, 1e17);
    dp[0] = 0;

    RMQ L(n + 1), R(n + 1);
    L.update(0, 0);
    R.update(0, 0);

    for (int i = 0; i < n; i++) {
        // T >= 2 * (ds[i] - ds[j])
        int ok = i;
        int ng = -1;
        while (ok - ng > 1) {
            int mid = (ok + ng) / 2;
            if (T >= 2 * (ds[i] - ds[mid])) ok = mid;
            else ng = mid;
        }
        chmin(dp[i + 1], L.minimum(ok, i + 1));
        chmin(dp[i + 1], R.minimum(0, ok) + 2 * ds[i]);

        L.update(i + 1, dp[i + 1] + T);
        R.update(i + 1, dp[i + 1] - ds[i]);
    }

    cout << dp[n] + E << endl;
}

