ll dfs(ll l){
	if(mem[l]!=-1)return mem[l];
	if(l==n-1)return T;
	if(l==n)return 0;
	ll res=T+(x[l+1]-x[l])+dfs(l+1);
	repl(r,l+1,n){
		ll time=(x[r]-x[l])*2;
		ll sum=0;
		if(time>T){
			sum+=time;
		}else{
			sum+=T-time;
		}
		sum+=(x[r+1]-x[l]);
		minch(res,sum+dfs(r+1));
	}
	return mem[l]=res;
}

