#include <iostream>
#include <vector>
#include <functional>

template <class T>
struct SegmentTree {
    using Merger = std::function<T(T, T)>;

    int length;
    std::vector<T> dat;
    T unit;
    Merger merge;

    explicit SegmentTree(int n, T unit, Merger merge)
        : length(1), unit(unit), merge(merge) {
        while (length < n) length <<= 1;
        dat.assign(length * 2, unit);
    }

    T query(int ql, int qr) {
        ql = std::max(ql, 0);
        qr = std::min(qr, length);
        ql += length, qr += length;

        T lacc = unit, racc = unit;
        while (ql < qr) {
            if (ql & 1) {
                lacc = merge(lacc, dat[ql]);
                ++ql;
            }
            if (qr & 1) {
                --qr;
                racc = merge(dat[qr], racc);
            }
            ql >>= 1, qr >>= 1;
        }
        return merge(lacc, racc);
    }

    void update(int nidx, T elem) {
        nidx += length;
        dat[nidx] = elem;

        while (nidx > 0) {
            nidx >>= 1;
            T vl = dat[nidx * 2 + 0];
            T vr = dat[nidx * 2 + 1];
            dat[nidx] = merge(vl, vr);
        }
    }
};

using lint = long long;
constexpr lint INF = 1LL << 50;

void solve() {
    int n;
    lint e, t;
    std::cin >> n >> e >> t;

    std::vector<lint> xs(n);
    for (auto& x : xs) {
        std::cin >> x;
        x *= 2;
    }
    xs.push_back(e * 2);

    std::vector<int> ls(n);
    {
        int l = 0;
        for (int i = 0; i < n; ++i) {
            while (xs[i] - xs[l] > t) ++l;
            ls[i] = l;
        }
    }

    SegmentTree<lint>
        near_seg(n + 1, INF,
                 [](auto a, auto b) { return std::min(a, b); }),
        far_seg(n + 1, -INF,
                [](auto a, auto b) { return std::max(a, b); });

    auto update = [&](int i, lint c) {
        near_seg.update(i, c);
        far_seg.update(i, xs[i] - c);
    };

    update(0, 0);
    for (int i = 0; i < n; ++i) {
        lint nc = near_seg.query(ls[i], i + 1) + t;
        lint fc = xs[i] - far_seg.query(0, ls[i]);

        auto c = std::min(nc, fc);
        update(i + 1, c);
    }

    lint ans = e + near_seg.query(n, n + 1);
    std::cout << ans << std::endl;
}

int main() {
    std::cin.tie(nullptr);
    std::cout.tie(nullptr);
    std::ios::sync_with_stdio(false);

    solve();

    return 0;
}

