	if (revFlag)G[to].push_back(Edge(from, revCap, (ll)G[from].size() - 1));//æœ€å°ã‚«ãƒƒãƒˆã®å ´åˆé€†è¾ºã¯0ã«ã™ã‚‹
}

ll max_flow_dfs(Graph& G, ll v, ll t, ll f, vector<bool>& used)
{
	if (v == t)
		return f;
	used[v] = true;
	for (int i = 0; i < G[v].size(); ++i) {
		Edge& e = G[v][i];
		if (!used[e.to] && e.cap > 0) {
			ll d = max_flow_dfs(G, e.to, t, min(f, e.cap), used);
			if (d > 0) {
				e.cap -= d;
				G[e.to][e.rev].cap += d;
				return d;
			}
		}
	}
	return 0;
}
//äºŒåˆ†ã‚°ãƒ©ãƒ•ã®æœ€å¤§ãƒžãƒƒãƒãƒ³ã‚°ã‚’æ±‚ã‚ãŸã‚Šã‚‚å‡ºæ¥ã‚‹ã€€ã¾ãŸäºŒéƒ¨ã‚°ãƒ©ãƒ•ã®æœ€å¤§ç‹¬ç«‹é›†åˆã¯é ‚ç‚¹æ•°-æœ€å¤§ãƒžãƒƒãƒãƒ³ã‚°ã®ã‚µã‚¤ã‚º
ll max_flow(Graph& G, ll s, ll t)//O(V(V+E))
{
	ll flow = 0;
	for (;;) {
		vector<bool> used(G.size());
		REP(i, used.size())used[i] = false;
		ll f = max_flow_dfs(G, s, t, INF, used);
		if (f == 0) {
			return flow;
		}
		flow += f;
	}
}
void BellmanFord(Graph& G, ll s, Array& d, Array& negative) {//O(|E||V|)
	d.resize(G.size());
	negative.resize(G.size());
	REP(i, d.size())d[i] = INF;
	REP(i, d.size())negative[i] = false;
	d[s] = 0;
	REP(k, G.size() - 1) {
		REP(i, G.size()) {
			REP(j, G[i].size()) {
				if (d[i] != INF && d[G[i][j].to] > d[i] + G[i][j].cap) {
					d[G[i][j].to] = d[i] + G[i][j].cap;
				}
			}
		}
	}
	REP(k, G.size() - 1) {
		REP(i, G.size()) {
			REP(j, G[i].size()) {
				if (d[i] != INF && d[G[i][j].to] > d[i] + G[i][j].cap) {
					d[G[i][j].to] = d[i] + G[i][j].cap;
					negative[G[i][j].to] = true;
				}
				if (negative[i] == true)negative[G[i][j].to] = true;
			}
		}
	}
}

void Dijkstra(Graph& G, ll s, Array& d) {//O(|E|log|V|)
	d.resize(G.size());
	REP(i, d.size())d[i] = INF;
	d[s] = 0;
	priority_queue<P, vector<P>, greater<P>> q;
	q.push(make_pair(0, s));
	while (!q.empty()) {
		P a = q.top();
		q.pop();
		if (d[a.second] < a.first)continue;
		REP(i, G[a.second].size()) {
			Edge e = G[a.second][i];
			if (d[e.to] > d[a.second] + e.cap) {
				d[e.to] = d[a.second] + e.cap;
				q.push(make_pair(d[e.to], e.to));
			}
		}
	}
}
void WarshallFloyd(Graph& G, Matrix& d) {//O(V^3)
	d.resize(G.size());
	REP(i, d.size())d[i].resize(G.size());
	REP(i, d.size()) {
		REP(j, d[i].size()) {
			d[i][j] = ((i != j) ? INF : 0);
		}
	}
	REP(i, G.size()) {
		REP(j, G[i].size()) {
			chmin(d[i][G[i][j].to], G[i][j].cap);
		}
	}
	REP(i, G.size()) {
		REP(j, G.size()) {
			REP(k, G.size()) {
				chmin(d[j][k], d[j][i] + d[i][k]);
			}
		}
	}
}
bool tsort(Graph& graph, vector<int>& order) {//ãƒˆãƒãƒ­ã‚¸ã‚«ãƒ«ã‚½ãƒ¼ãƒˆO(E+V)
	int n = graph.size(), k = 0;
	Array in(n);
	for (auto& es : graph)
		for (auto& e : es)in[e.to]++;
	priority_queue<ll, Array, greater<ll>> que;
	REP(i, n)
		if (in[i] == 0)que.push(i);
	while (que.size()) {
		int v = que.top();
		que.pop();
		order.push_back(v);
		for (auto& e : graph[v])
			if (--in[e.to] == 0)que.push(e.to);
	}
	if (order.size() != n)return false;
	else return true;
}
class lca {
public:
	const int n = 0;
	const int log2_n = 0;
	std::vector<std::vector<int>> parent;
	std::vector<int> depth;

	lca() {}

	lca(const Graph& g, int root)
		: n(g.size()), log2_n(log2(n) + 1), parent(log2_n, std::vector<int>(n)), depth(n) {
		dfs(g, root, -1, 0);
		for (int k = 0; k + 1 < log2_n; k++) {
			for (int v = 0; v < (int)g.size(); v++) {
				if (parent[k][v] < 0)
					parent[k + 1][v] = -1;
				else
					parent[k + 1][v] = parent[k][parent[k][v]];
			}
		}
	}

	void dfs(const Graph& g, int v, int p, int d) {
		parent[0][v] = p;
		depth[v] = d;
		for (auto& e : g[v]) {
			if (e.to != p) dfs(g, e.to, v, d + 1);
		}
	}

	int get(int u, int v) {
		if (depth[u] > depth[v]) std::swap(u, v);
		for (int k = 0; k < log2_n; k++) {
			if ((depth[v] - depth[u]) >> k & 1) {
				v = parent[k][v];
			}
		}
		if (u == v) return u;
		for (int k = log2_n - 1; k >= 0; k--) {
			if (parent[k][u] != parent[k][v]) {
				u = parent[k][u];
				v = parent[k][v];
			}
		}
		return parent[0][u];
	}
};
void visit(const Graph& g, int v, Matrix& scc, stack<ll>& S, Array& inS, Array& low, Array& num, int& time) {
	low[v] = num[v] = ++time;
	S.push(v); inS[v] = true;
	FOR(e, g[v]) {
		int w = e->to;
		if (num[w] == 0) {
			visit(g, w, scc, S, inS, low, num, time);
			low[v] = min(low[v], low[w]);
		}
		else if (inS[w])
			low[v] = min(low[v], num[w]);
	}
	if (low[v] == num[v]) {
		scc.push_back(Array());
		while (1) {
			int w = S.top(); S.pop(); inS[w] = false;
			scc.back().push_back(w);
			if (v == w) break;
		}
	}
}
void stronglyConnectedComponents(const Graph& g, Matrix& scc) {//å¼·é€£çµæˆåˆ†åˆ†è§£ O(E+V)
	const int n = g.size();
	Array num(n), low(n);
	stack<ll> S;
	Array inS(n);
	int time = 0;
	REP(u, n) if (num[u] == 0)
		visit(g, u, scc, S, inS, low, num, time);
}

class UnionFind {
	vector<int> data;
	ll num;
public:
	UnionFind(int size) : data(size, -1), num(size) { }
	bool unite(int x, int y) {//xã¨yã®é›†åˆã‚’çµ±åˆã™ã‚‹
		x = root(x); y = root(y);
		if (x != y) {
			if (data[y] < data[x]) swap(x, y);
			data[x] += data[y]; data[y] = x;
		}
		num -= (x != y);
		return x != y;
	}
	bool findSet(int x, int y) {//xã¨yãŒåŒã˜é›†åˆã‹è¿”ã™
		return root(x) == root(y);
	}
	int root(int x) {//xã®ãƒ«ãƒ¼ãƒˆã‚’è¿”ã™
		return data[x] < 0 ? x : data[x] = root(data[x]);
	}
	ll size(int x) {//xã®é›†åˆã®ã‚µã‚¤ã‚ºã‚’è¿”ã™
		return -data[root(x)];
	}
	ll numSet() {//é›†åˆã®æ•°ã‚’è¿”ã™
		return num;
	}
};
class SumSegTree {
private:

	ll _sum(ll a, ll b, ll k, ll l, ll r) {
		if (r <= a || b <= l)return 0;    // äº¤å·®ã—ãªã„
		if (a <= l && r <= b)return dat[k];   // a,l,r,bã®é †ã§å®Œå…¨ã«å«ã¾ã‚Œã‚‹
		else {
			ll s1 = _sum(a, b, 2 * k + 1, l, (l + r) / 2); // å·¦ã®å­
			ll s2 = _sum(a, b, 2 * k + 2, (l + r) / 2, r); // å³ã®å­
			return s1 + s2;
		}
	}
public:
	ll n, height;
	vector<ll> dat;

	// åˆæœŸåŒ–ï¼ˆ_nã¯æœ€å¤§è¦ç´ æ•°ï¼‰
	SumSegTree(ll _n) {
		n = 1;
		height = 1;
		while (n < _n) {
			n *= 2;
			height++;
		}
		dat = vector<ll>(2 * n - 1, 0);
	}

	// å ´æ‰€i(0-indexed)ã«xã‚’è¶³ã™
	void add(ll i, ll x) {
		i += n - 1; // iç•ªç›®ã®è‘‰ãƒŽãƒ¼ãƒ‰ã¸
		dat[i] += x;
		while (i > 0) { // ä¸‹ã‹ã‚‰ä¸ŠãŒã£ã¦ã„ã
			i = (i - 1) / 2;
			dat[i] += x;
		}
	}

	// åŒºé–“[a,b)ã®ç·å’Œã€‚ãƒŽãƒ¼ãƒ‰k=[l,r)ã«ç€ç›®ã—ã¦ã„ã‚‹ã€‚
	ll sum(ll a, ll b) {
		return _sum(a, b, 0, 0, n);
	}
};
class RmqTree {
private:

	ll _find(ll a, ll b, ll k, ll l, ll r) {
		if (r <= a || b <= l)return INF;    // äº¤å·®ã—ãªã„
		if (a <= l && r <= b)return dat[k];   // a,l,r,bã®é †ã§å®Œå…¨ã«å«ã¾ã‚Œã‚‹
		else {
			ll s1 = _find(a, b, 2 * k + 1, l, (l + r) / 2); // å·¦ã®å­
			ll s2 = _find(a, b, 2 * k + 2, (l + r) / 2, r); // å³ã®å­
			return min(s1, s2);
		}
	}
public:
	ll n, height;
	vector<ll> dat;

	// åˆæœŸåŒ–ï¼ˆ_nã¯æœ€å¤§è¦ç´ æ•°ï¼‰
	RmqTree(ll _n) {
		n = 1;
		height = 1;
		while (n < _n) {
			n *= 2;
			height++;
		}
		dat = vector<ll>(2 * n - 1, INF);
	}

	// å ´æ‰€i(0-indexed)ã‚’xã«ã™ã‚‹
	void update(ll i, ll x) {
		i += n - 1; // iç•ªç›®ã®è‘‰ãƒŽãƒ¼ãƒ‰ã¸
		dat[i] = x;
		while (i > 0) { // ä¸‹ã‹ã‚‰ä¸ŠãŒã£ã¦ã„ã
			i = (i - 1) / 2;
			dat[i] = min(dat[i * 2 + 1], dat[i * 2 + 2]);
		}
	}

	// åŒºé–“[a,b)ã®æœ€å°å€¤ã€‚ãƒŽãƒ¼ãƒ‰k=[l,r)ã«ç€ç›®ã—ã¦ã„ã‚‹ã€‚
	ll find(ll a, ll b) {
		return _find(a, b, 0, 0, n);
	}
};


