	cont (i, n) {
		cin >> a[i];
	}
	tr.init();
	int j = 0;
	tr.upd(0, 0, dp[0] - a[0] - 2 * a[1] + max(0LL, t - 2 * (a[0] - a[1])));
	cont (i, n) {
		for (; j < i && f(i, j) <= 0; tr.upd(j, j, -f(i - 1, j)), ++j);
		tr.upd(j, i - 1, -2 * (a[i] - a[i - 1]));
		pair<num, int> tmp = tr.qry(0, i - 1);
		int p = tmp.second;
		dp[i] = calc(i, p);
		tr.upd(i, i, dp[i] - a[i] - 2 * a[i + 1] + max(0LL, t - 2 * (a[i] - a[i + 1])));
	}
	ans = dp[n] + m - a[n];
	cout << ans << ln;
}

