struct Seg{
    ll d[2 * sz];
    void ini(){ fill(d, d + 2 * sz, INF); }
    void upd(int x, ll v){
        x += sz;
        d[x] = min(d[x], v);
        for(x >>= 1; x; x >>= 1) d[x] = min(d[2 * x], d[2 * x + 1]);
    }
    ll get(int s, int e){
        ll r = INF;
        for(s += sz, e += sz; s <= e; s >>= 1, e >>= 1){
            if( s & 1) r = min(r, d[s++]);
            if(~e & 1) r = min(r, d[e--]);
