#define INF 1LL<<60
typedef long long ll;
template<class V, int NV> class SegTree {
public:
	static V const def = -(1LL << 60);
	V comp(V l, V r) { return max(l, r); };

	vector<V> val;
	SegTree() { val = vector<V>(NV * 2, def); }

	V getval(int l, int r) { //[l,r]
		l += NV; r += NV + 1;
		V ret = def;
		while (l < r) {
			if (l & 1) ret = comp(ret, val[l++]);
			if (r & 1) ret = comp(ret, val[--r]);
			l /= 2; r /= 2;
		}
		return ret;
	}
	void update(int i, V v) {
		i += NV;
		val[i] = v;
		while (i>1) i >>= 1, val[i] = comp(val[i * 2], val[i * 2 + 1]);
	}
};
//-----------------------------------------------------------------
int N;
ll E, T;
ll X[101010];
ll Y[101010];
ll YY[101010];
ll dp[101010][2];
SegTree<ll, 1 << 20> st;
//-----------------------------------------------------------------
int main() {
	cin >> N >> E >> T;
	rep(i, 0, N) scanf("%d", &X[i]);
	X[N] = E;

	Y[0] = X[0];
	rep(i, 0, N) Y[i + 1] = X[i + 1] - X[i];

	YY[0] = Y[0];
	rep(i, 1, N + 1) YY[i] = Y[i] + YY[i - 1];

	rep(i, 0, N + 1) dp[i][0] = dp[i][1] = INF;

	dp[0][0] = Y[0];
	dp[0][1] = Y[0] + T;
	st.update(0, dp[0][0] - YY[0] * 3);
	rep(i, 1, N) {
		dp[i][0] = min(dp[i][0], dp[i - 1][0] + Y[i] + T);
		dp[i][0] = min(dp[i][0], dp[i - 1][1] + Y[i]);
		dp[i][1] = min(dp[i][1], dp[i - 1][0] + Y[i] + T * 2);
		dp[i][1] = min(dp[i][1], dp[i - 1][1] + Y[i] + T);
		dp[i][1] = min(dp[i][1], YY[i] * 3 + st.getval(0, i - 1));
		st.update(i, dp[i][0] - YY[i] * 3);
	}

	//rep(i, 0, N + 1) printf("dp[%d][0] = %lld, dp[%d][1] = %lld\n", i, dp[i][0], i, dp[i][1]);

	cout << dp[N - 1][1] + Y[N] << endl;
}

