ll cost(int i , int opt)
{
	ll prev = 0;

	prev = dp[opt - 1];

	ll cur = max((a[i] - a[opt]) * 2 , T);

	return prev + cur;
}
int findOpt(int idx)
{
	return lower_bound(a , a + N , a[idx] - T / 2) - a;
}
