{
	int opt = findOpt(idx);

	ll dp1 = T;

	if(opt - 1 >= 0)
		dp1 += dp[opt - 1];

	ll dp2 = a[idx] * 2;

	if(opt - 1 >= 0)
		dp2 += min(dpRMQ[opt - 1] , 0LL);
	else
		return dp1;
	return(min(dp1 , dp2));

}
