const int maxn = 1e5 + 100;
const ll inf = 2e18, mod = 1e9 + 7;

int n;
ll T, E, x[maxn], dp[maxn], seg[4 * maxn], lazy[4 * maxn];

inline void pushDown(int u, int l, int r)
{
	if (r - l > 1)
	{
		seg[2 * u] += lazy[u];
		seg[2 * u + 1] += lazy[u];
		lazy[2 * u] += lazy[u];
		lazy[2 * u + 1] += lazy[u];
	}
	lazy[u] = 0;
}

void update(int u, int l, int r, int b, int e, ll val)
{
	if (lazy[u])
		pushDown(u, l, r);
	if (b <= l && r <= e)
	{
		seg[u] += val;
		lazy[u] += val;
		pushDown(u, l, r);
		return;
	}
	int mid = (l + r) / 2;
	if (b < mid)
		update(2 * u, l, mid, b, e, val);
	if (e > mid)
		update(2 * u + 1, mid, r, b, e, val);
	seg[u] = min(seg[2 * u], seg[2 * u + 1]);
}

void updateSet(int u, int l, int r, int x, ll val)
{
	if (lazy[u])
		pushDown(u, l, r);
	if (r - l == 1)
	{
		seg[u] = val;
		return;
	}
	int mid = (l + r) / 2;
	if (x < mid)
		updateSet(2 * u, l, mid, x, val);
	else
		updateSet(2 * u + 1, mid, r, x, val);
	seg[u] = min(seg[2 * u], seg[2 * u + 1]);
}

ll query(int u, int l, int r, int b, int e)
{
	if (lazy[u])
		pushDown(u, l, r);
	if (b <= l && r <= e)
	{
		//cerr << "QUERY " << l << ' ' << r << ':' << seg[u] << endl;
		return seg[u];
	}
	int mid = (l + r) / 2;
	ll res = inf;
	if (b < mid)
		res = min(res, query(2 * u, l, mid, b, e));
	if (e > mid)
		res = min(res, query(2 * u + 1, mid, r, b, e));
	return res;
}

int main()
{
	ios_base::sync_with_stdio(false);
	cin.tie(0);

	fill(seg, seg + 4 * maxn, inf);

	cin >> n >> E >> T;
	for (int i = 0; i < n; i++)
		cin >> x[i];
	int pos = 0;
	for (int i = 0; i < n; i++)
	{
		if (pos)
			update(1, 0, n + 2, 0, pos + 1, 2 * (x[i] - x[i - 1]));
		updateSet(1, 0, n + 2, i + 1, T + dp[i]);
		while (2 * (x[i] - x[pos]) >= T)
		{
			updateSet(1, 0, n + 2, pos + 1, 2 * (x[i] - x[pos]) + dp[pos]);
			pos++;
		}
		//cerr << "___________ " << i + 1 << endl;
		dp[i + 1] = query(1, 0, n + 2, 0, i + 2);
	}
	for (int i = 0; i <= n; i++)
		cerr << '_' << dp[i];
	cerr << endl;
	cout << dp[n] + E << endl;
}

