        while (T < 2 * (x[lidx - 1] - x[i]))rmin = min(rmin, dp[lidx] + 2 * (x[lidx - 1] - x[i])), lidx--;
        dp[i] = min(rmin, dp[lidx] + T);
    }
    cout << E + dp[0] << endl;;
}

