inline node merge(node a, node b) {
	return {min(a.mn_d, b.mn_d), max(a.mx_d, b.mx_d), min(a.mn_x, b.mn_x), min(a.mn_y, b.mn_y)};
}

void update(int p, long long x, long long d, int id = 1, int st = 0, int en = n) {
	if (en - st == 1) {
		seg[id] = {d, d, x, x + d};
		return;
	}
	int mid = st + en >> 1;
	if (p < mid)
		update(p, x, d, id << 1, st, mid);
	else
		update(p, x, d, id << 1 | 1, mid, en);
	seg[id] = merge(seg[id << 1], seg[id << 1 | 1]);
}

long long get(int l, int r, int d, int id = 1, int st = 0, int en = n) {
	if (r <= st || en <= l)
		return 1e18;
	if (l <= st && en <= r) {
		if (seg[id].mx_d <= d)
			return seg[id].mn_x + d;
		if (seg[id].mn_d >= d)
			return seg[id].mn_y;
	}
	int mid = st + en >> 1;
	return min(get(l, r, d, id << 1, st, mid), get(l, r, d, id << 1 | 1, mid, en));
}

