inline void solve() {
	for (int i = 0; i < n; i++) {
		update(i, dp[i] - x[i - 1] - 2 * x[i], t + 2LL * x[i]);
		dp[i + 1] = get(0, i + 1, 2 * x[i]) + x[i];
	}
	cout << dp[n] + e - x[n - 1];
}

