struct Segment_tree{
    using T=ll;

    int n;
    vector<T> data;
    const T out=(1LL<<50)-1;

    inline T vmerge(T l,T r){return min(l,r);}

    Segment_tree(int n):n(n){data.assign(2*n,out);}

    void update(int p,T x) {  // set value at position p
        for(data[p+=n]=x; p > 1; p >>= 1) data[p>>1]=vmerge(data[p],data[p^1]);
    }

    T query(int l, int r) {  // sum on interval [l, r)
        T resl = out,resr = out;
        for (l += n, r += n; l < r; l >>= 1, r >>= 1) {
            if (l&1) resl = vmerge(data[l++], resl);
            if (r&1) resr = vmerge(resr, data[--r]);
        }
        return vmerge(resl,resr);
    }
};


