    ll ret = INF;
    if(L<=mid)ret = min(ret,query(l,mid,rt<<1,L,min(mid,R),f));
    if(R>mid)ret = min(ret,query(mid+1,r,rt<<1|1,max(L,mid+1),R,f));
    return ret;
}
