#include <bits/stdc++.h>
using namespace std;
const long long inf = 1ll * 1000 * 1000 * 1000 * 1000 * 1000;
const int N = 1e5 + 10; 
int a[N], nxt[N], n, e, t;
long long val;
long long seg[N << 2], lazy[N << 2], dp[N];
vector<int> fnd[N];
void relax(int id) {
	int lc = id * 2, rc = lc + 1;
	seg[lc] += lazy[id], lazy[lc] += lazy[id];
	seg[rc] += lazy[id], lazy[rc] += lazy[id];
	lazy[id];
	return;
}
void add(int l, int r, int id = 1, int s = 0, int e = n) {
	if (l == r)
		return;
	if (l <= s && e <= r) {
		seg[id] += val;
		lazy[id] += val;
		return;
	}
	if (e <= l || r <= s)
		return;
	int mid = (s + e) / 2, lc = id * 2, rc = lc + 1;
	relax(id);
	add(l, r, lc, s, mid);
	add(l, r, rc, mid, e);
	seg[id] = min(seg[lc], seg[rc]);
}
void upd(int p, int id = 1, int s = 0, int e = n) {
	if (e - s == 1) {
		seg[id] = val;
		lazy[id] = 0;
		return;
	}
	int mid = (s + e) / 2, lc = id * 2, rc = lc + 1;
	if (p < mid)
		upd(p, lc, s, mid);
	else
		upd(p, rc, mid, e);
	seg[id] = min(seg[lc], seg[rc]);
	return;
}
int main() {
	ios_base::sync_with_stdio (false), cin.tie(0), cout.tie(0);
	cin >> n >> e >> t;
	for (int i = 1; i <= n; i++)
		cin >> a[i];
	int st = -1;
	n++;
	for (int i = 0; i < n; i++) {
		val = inf;
		upd(i);
		while ((a[i] - a[st + 1]) * 2 >= t)
			st++;
		if (st != -1)
			fnd[st].push_back(i);
	}
	st = n;
	for (int i = n - 1; i >= 0; i--) {
		while ((a[st - 1] - a[i]) >= t)
			st--;
		nxt[i] = st;
	}
//	cout << "HO" << endl;
	val = t;
	upd(n - 1);
	for (int i = n - 2; i >= 0; i--) {
//		cout << "i = " << i << endl;
		long long toall = a[i + 1] - a[i], tosome = a[i + 2] - a[i + 1];
		val = toall, add(i + 1, n);
		if (tosome > 0)
			val = tosome, add(nxt[i + 1], n);
		for (auto plc : fnd[i + 1]) {
			long long dis = 1ll * (a[plc] - a[i + 1]) * 3 + 1ll * toall;
			val = dis + dp[plc], upd(plc);
//			cout << "plc = " << plc << " : " << dis << " val = " << val << endl;
		}
		dp[i] = seg[1], val = dp[i] + t;
//		cout << "i = " << i << " : " << a[i] << " : " << dp[i] << " toall = " << toall << " tosome = " << tosome << " : " << nxt[i + 1] << endl;
		upd(i);
	}
	cout << dp[0] + e - a[n - 1];
	return 0;
}

#include<bits/stdc++.h>
using namespace std;

const int N=1e5+5;
const long long INF=100000000000ll;
int n,T,E;
int a[N],p[N];

struct Tree{
	int left,right;
	long long val,tag;
	Tree *left_child,*right_child;
};

void build(Tree *pos,int x,int y){
	pos->left=x;pos->right=y;pos->val=pos->tag=0ll;
	if(x==y) return;
	int mid=(x+y)>>1;
	pos->left_child=new Tree();
	pos->right_child=new Tree();
	build(pos->left_child,x,mid);
	build(pos->right_child,mid+1,y);
}

long long VAL(Tree *pos){
	return pos->val+pos->tag;
}

void pushdown(Tree *pos){
	pos->left_child->tag+=pos->tag;
	pos->right_child->tag+=pos->tag;
	pos->tag=0ll;
}

void pushup(Tree *pos){
	pos->tag=0ll;
	pos->val=min(VAL(pos->left_child),VAL(pos->right_child));
}

long long query(Tree *pos,int x,int y){
	if(x>y) return INF;
	if(pos->left>=x&&pos->right<=y) return VAL(pos);
	if(pos->left>y||pos->right<x) return INF;
	pushdown(pos);
	long long ret=min(query(pos->left_child,x,y),query(pos->right_child,x,y));
	pushup(pos);
	return ret;
}

void modify(Tree *pos,int x,int y,long long v){
	if(pos->left>=x&&pos->right<=y){
		pos->tag+=v;
		return;
	}
	if(pos->left>y||pos->right<x) return;
	pushdown(pos);
	modify(pos->left_child,x,y,v);
	modify(pos->right_child,x,y,v);
	pushup(pos);
}

Tree *rt,*dp;

int main(){
	scanf("%d%d%d",&n,&E,&T);
	for(int i=1;i<=n;i++) scanf("%d",a+i);
	for(int i=1;i<=n;i++){
		int l=0,r=i;
		while(l<=r){
			int mid=(l+r)>>1;
			if((a[i]-a[mid])*2<=T) r=mid-1;
			else l=mid+1;
		}
		p[i]=r;
	}
//	for(int i=1;i<=n;i++) printf("%d ",p[i]);puts("");
	rt=new Tree();
	dp=new Tree();
	build(rt,0,n);
	build(dp,0,n);
	for(int i=1;i<=n;i++){
		if(i!=1) modify(rt,0,i-2,2ll*(a[i]-a[i-1]));
		long long DP=min(query(rt,0,p[i]-1),query(dp,p[i],i-1)+T);
//		printf("%d %lld\n",i,DP);
		modify(dp,i,i,DP);
		modify(rt,i,i,DP);
	}
	printf("%lld\n",query(rt,n,n)+E);
	return 0;
}

