    T vl=ti,vr=ti;
    for(int l=a+n,r=b+n;l<r;l>>=1,r>>=1) {
      if(l&1) vl=f(vl,dat[l++]);  //å·¦ç«¯ã‚’ä½¿ã„ãŸããªã‚‹ã®ã¯ãƒ–ãƒ­ãƒƒã‚¯ãŒå¥‡æ•°ã®æ™‚
      if(r&1) vr=f(dat[--r],vr);  //å³ç«¯ã€ƒå¶æ•°ã®æ™‚ï¼ˆé–‹åŒºé–“ãªã®ã§r:å¥‡æ•°ã§åå¿œï¼‰
    }
