    if(m<=st) return find(st,check,acc,(k<<1)|1,m,r);  //mãŒstã‚ˆã‚Šå¾Œã‚ãªã‚‰æ˜Žã‚‰ã‹ã«l=m;
    if(st<=l&&!check(f(acc,dat[k]))){  //ä»Šè¦‹ã¦ã‚‹kå…¨éƒ¨ã§ã‚‚ãƒ€ãƒ¡(mã ã¨è¶³ã‚Šãªã„ã®ã§l=mã«ã™ã‚‹ï¼‰
      acc=f(acc,dat[k]);  //kã¯å…¨éƒ¨accã«åŠ ãˆãŸä¸Šã§-1ã‚’è¿”ã™
