// dp(i) = min((x[i]-x[i-1])+T,min((x[i]-x[i-1])+dp(i+1),(x[i+1]-x[i])+dp(i-1)))
// ä¸Šã®å ´åˆã€0<i<Nã§ãªã„ã¨ç¯„å›²å¤–ã‚¢ã‚¯ã‚»ã‚¹ ã‚¹ã‚¿ãƒƒã‚¯ã‚ªãƒ¼ãƒãƒ¼ãƒ•ãƒ­ãƒ¼ï¼Ÿ
int do_dp() {
	// ãƒ¡ãƒ¢åŒ–å†å¸°ã˜ã‚ƒã ã‚ï¼Ÿ
	/*if (i == 0) { return INF; }
	if (memo[i] != -1) { return memo[i]; }
	else {
		if (i == N - 1) {
			return memo[i] = std::min((x[i] - x[i - 1]) + T, (x[i] - x[i - 1]) + dp(i - 1));
		}
		return memo[i] = std::min((x[i] - x[i - 1]) + T, std::min((x[i + 1] - x[i]) + dp(i + 1), (x[i] - x[i - 1]) + dp(i - 1)));
	}*/

	// ã¨ã«ã‹ããƒ«ãƒ¼ãƒ—
	for (int i = 0; i < N; ++i) {
		if (i == N - 1) { dp[i] = std::min((x[i] - x[i - 1]) + T, (x[i] - x[i - 1]) + dp[i - 1]); }
		else { dp[i] = std::min((x[i] - x[i - 1]) + T, std::min((x[i + 1] - x[i]) + dp[i + 1], (x[i] - x[i - 1]) + dp[i - 1])); }
	}

	return dp[N - 1];
}

