vector<int> tree;
int N = 1;

void upd(int v) {
	if(v == 0) 
		return;
	tree[v] = max(tree[v * 2], tree[v * 2 + 1]);
	upd(v / 2);
}

int get(int v, int l, int r, int vl, int vr) {
	if(vr <= l || r <= vl)
		return inf;
	if(vl <= l && r <= vr) {
		return tree[v];
	}
	int m = (l + r) / 2;
	return min(get(v * 2, l, m, vl, vr), get(v * 2 + 1, m, r, vl, vr));
}

