#define MAXN 100100
#define inf 1000000000000000000ll
using namespace std;

ll treeA[4*MAXN],treeB[4*MAXN],Dp[MAXN],e,t,a[MAXN],b[MAXN];
int n,p[MAXN],m;

void fModify(int x,ll y,ll tree[])
{
	x+=m-1;
	tree[x]=y;
	while(x>0)
	{	
		x=(x-1)/2;
		tree[x]=min(tree[2*x+1],tree[2*x+2]);
	}
}

void fInit()
{
	a[0]=0;
	Dp[0]=0;
	n++;m=1;
	while (m<n)m*=2;
	fModify(0,0,treeA);
	fModify(0,-2*a[1],treeB);
}

ll fQuery(int x,int y,int l,int r,int k,ll tree[])
{                       
	if(x>=y)return inf;
	if(l>=y||r<=x)return inf;
	if(x<=l&&r<=y)return tree[k];
	else
	{
		int mid=(l+r)/2;
		ll lx=fQuery(x,y,l,mid,2*k+1,tree);
		ll ly=fQuery(x,y,mid,r,2*k+2,tree);
		return min(lx,ly);
	}
}

void fChange(int x)
{
	Dp[x]+=a[x];
	int P=p[x];                        
	ll res1=fQuery(0,P,0,m,0,treeB)+b[x];
	ll res2=fQuery(max(P,0),x,0,m,0,treeA)+t;

	Dp[x]+=min(res1,res2);
	fModify(x,Dp[x]-a[x],treeA);

	fModify(x,Dp[x]-a[x]-a[x+1]*2,treeB);
}


