	for(int i=0;i<n;i++)b[i]=2*a[i];
	for(int i=1;i<n;i++)p[i]=lower_bound(b,b+i+1,b[i]-t)-b-1;
	for(int i=1;i<n;i++)fChange(i);//cout<<Dp[i]<<" "<<i<<endl;


	printf("%lld\n",Dp[n-1]-a[n-1]+e);


	return 0;
}

