const int MAXN = 100010;
ll n, T, E;

ll arr[MAXN];

ll memo_small[MAXN];
ll dp_small(int id){
    if (id == n) return E - arr[n-1];
    ll &res = memo_small[id];
    if (res != -1) return res;
    ll pre = arr[id];
    if (id) pre = arr[id] - arr[id-1];

    res = LLONG_MAX/2;
    for (int i = id; i < n; i++){
        ll d = arr[i] - arr[id];
        ll cur = pre + max(2LL * d, T) + d + dp_small(i + 1);
        if (cur < res) res = cur;
    }
    return res;
}

void solve_small(){
    memset(memo_small, -1, sizeof memo_small);
    ll res = dp_small(0);
    cout << res << endl;
}

ll memo[MAXN];
ll dp(int id){
    if (id == n) return E - arr[n-1];
    ll &res = memo[id];
    if (res != -1) return res;
    ll pre = arr[id];
    if (id) pre = arr[id] - arr[id-1];

    int i;
    for (i = id; i < n; i++){
        ll d = arr[i] - arr[id];
        if (2LL * d > (i - id + 1) * T) break;
    }
    ll d = arr[i-1] - arr[id];
    res = pre + max(2LL * d, T) + d + dp(i);
    return res;
}
void solve_large(){
    memset(memo, -1, sizeof memo);
    ll res = dp(0);
    cout << res << endl;
}

