ll N, E, T;
vector<ll> X;

int main(void) {
    cin >> N >> E >> T;
    X.push_back(0);
    for (size_t i = 0; i < N; i++) {
        ll x;
        cin >> x;
        X.push_back(x);
    }

    vector<ll> dp(N + 1, 0);

    for (ll i = 1; i <= N; i++) {
        ll m = INF;
        for (ll j = 0; j < i; j++) {
            ll d = dp[j] + max(2 * (X[i] - X[j + 1]), T);
            if (d < m) {
                m = d;
            }
        }
        dp[i] = m;
    }

    cout << dp[N] + E << endl;
    return 0;
}

