#include <bits/stdc++.h>

using namespace std;

typedef long long ll;

ll D[101010], dp[101010];
ll T1[303030], T2[303030];
ll n, t, sz;

void update(ll* T, ll p, ll v)
{
	p += sz;
	T[p] = v;

	for(p>>=1;p;p>>=1){
		T[p] = min(T[p<<1], T[p<<1|1]);
	}
}

ll get_min(ll* T, ll l, ll r)
{
	ll ret = 1e18;
	l += sz, r += sz;

	for(;l<r;){
		if(l & 1) ret = min(ret, T[l]);
		if(~r & 1) ret = min(ret, T[r]);
		l = l+1>>1;
		r = r-1>>1;
	}
	if(l == r) ret = min(ret, T[l]);

	return ret;
}

int main()
{
	ll i, j;

	scanf("%lld", &n);
	scanf("%lld%lld", D+n+1, &t);

	for(i=1;i<=n;i++) scanf("%lld", D+i);

	for(sz=1;sz<=n;sz<<=1);

	for(i=1;i<=sz+n;i++){
		T1[i] = T2[i] = 1e18;
	}

	update(T1, 1, 0 - 2*D[1]);
	update(T2, 1, 0);
	for(i=1,j=1;i<=n;i++){
		for(;2 * (D[i]-D[j]) > t;j++);
		dp[i] = min(get_min(T1, 1, j-1) + 3*D[i], get_min(T2, j, i) + t + D[i]);
		update(T1, i+1, dp[i] - D[i] - 2*D[i+1]);
		update(T2, i+1, dp[i] - D[i]);
	}

