        forn(i, maxn) a[i] = infl;
    }
    void add(int i, i64 x) {
        for (; i < maxn; i |= (i+1)) {
            a[i] = min(a[i], x);
        }
    }
    i64 get(int i) {
        i64 mn = infl;
        for (; i >= 0; i = (i&(i+1))-1) {
            mn = min(mn, a[i]);
        }
