		int lb = -1, ub = i+1;
		while (ub - lb > 1) {
			int mi = (ub + lb) / 2;
			if (2 * (x[i] - x[mi]) > t)lb = mi;
			else ub = mi;
		}

		//[0,i]ã‚’åˆ†ã‘ã‚‹
		//[0,lb]ã¯2(x[i]-x[j]), [ub,i]ã¯t
		int X1, X2;
		X1 = dp1.get(ub, i+1) + t;
		X2 = dp2.get(0, ub) + 2 * x[i];
		dp1.update(i+1, min(X1, X2));
		dp2.update(i+1, min(X1,X2)-2 * x[i+1]);
	}
	cout << dp1.get(n, n+1)+e << endl;
}

