const int max_n = 1e6 + 20, inf = 1e12;
int a[max_n], DP[max_n];
pair<int, int> seg[max_n * 4];
int n, e, t, INF = 1e12, L;
pair<int, int> Merge(pii x, pii y) {
    if (x.X < y.X)
        return x;
    return y;
}
void update(int x, int val, int b = 0, int e = n, int id = 1) {
    if (e - b == 1) {
        seg[id].X = val;
        seg[id].Y = x;
     //   cout << id << ">>>>>>>>>" << val << "   ";
        return;
    }
    int mid = e + b >> 1;
    if (x >= mid)
        update(x, val, mid, e, id << 1 | 1);
    else
        update(x, val, b, mid, id << 1);
    seg[id] = Merge(seg[id << 1 | 1], seg[id << 1]);
   // cout << endl;
   // cout << (id << 1)<< " BBBBBBB " << seg[id << 1].X << endl;
}
pii get(int l, int r, int b = 0, int e = n, int id = 1) {
 //   cout << "A";
    if (e <= l || r <= b)
        return {INF, 0};
    if (l <= b && e <= r) {
      //  cout << "              " << seg[id].Y << endl;
        return seg[id];
    }
    int mid = b + e >> 1;
    return Merge(get(l, r, b, mid, id << 1), get(l, r, mid, e, id << 1 | 1));
}
void out() {
    for (int i = 1; i < n * 4; i++)
        cout << seg[i].Y << ">>>>>" << seg[i].X << "   ";
    cout << endl;
}
void solve() {
    for (int i = 1; i < n; i++) {
        int tmp = (lower_bound(a, a + n, a[i] - L) - a);
        pii x = get(tmp, i), y = get(0, tmp);
        //cout << tmp << endl;
        DP[i] = min(x.X + t, y.X + 2 * (a[i] - a[y.Y])) + a[i];
       // DP[i] = min(DP[i] , DP[i - 1] + a[i] - a[i - 1] + t);
  //      cout << DP[i] << endl;
        update(i, DP[i - 1] - a[i - 1]);
      //  cout << endl;
        //out();
    }
}
