#include <algorithm>
#include <iostream>
#include <cstring>
#include <vector>
using namespace std;

typedef long long ll;

const ll MAX_N = 1e5 + 5, inf = 1000000LL * 1000000LL * 1000000LL;
ll n, E, T;
ll x[MAX_N];
ll dp[MAX_N];
ll seg[MAX_N << 2];
ll lazy[MAX_N << 2];

void shift (ll id) {
	seg[id << 1] += lazy[id];
	lazy[id << 1] += lazy[id];

	seg[id << 1 | 1] += lazy[id];
	lazy[id << 1 | 1] += lazy[id];
	lazy[id] = 0;
}

void add (ll l, ll r, ll val, ll s = 0, ll e = n, ll id = 1) {
	if (l <= s && e <= r) {
		seg[id] += val;
		lazy[id] += val;
		return ;
	}

	if (r <= s || e <= l)
		return ;

	shift(id);

	ll mid = e + s >> 1;
	add(l, r, val, s, mid, id << 1);
	add(l, r, val, mid, e, id << 1 | 1);
	seg[id] = min(seg[id << 1], seg[id << 1 | 1]);
}

ll get (ll l, ll r, ll s = 0, ll e = n, ll id = 1) {
	if (l <= s && e <= r)
		return seg[id];

	if (r <= s || e <= l)
		return inf;

	shift(id);
	ll mid = e + s >> 1;
	return min(get(l, r, s, mid, id << 1), get(l, r, mid, e, id << 1 | 1));
}

int main() {
	ios::sync_with_stdio(false), cin.tie(0);
	cin >> n >> E >> T;
	for (ll i = 0; i < n; i++)
		cin >> x[i];

	dp[n - 1] = T;
	for (ll i = n - 2; ~i; i--) {
		add(i + 1, n, 2 * (x[i + 1] - x[i]));
		add(i, i + 1, dp[i + 1]);
		ll ind = lower_bound(x, x + n, x[i] + (T + 1) / 2) - x;
		ind--;

		dp[i] = T + dp[ind + 1];
		if (ind + 1 < n)
			dp[i] = min(dp[i], get(ind + 1, n));
	}

	cout << dp[0] + E << "\n";
	return 0;
}

