{
    fin >> n >> e >> t;
    for (int i = 1; i <= n; i++)fin >> x[i];

    sum = e;
    for (int i = 1; i < n; i++)
    {
        int ind = i + 1;
        d = x[ind] - x[i];
        while (d < t && ind <= n)
        {
            ind ++;
            d = x[ind] - x[i];
        }
        ind--;
        if (i == ind)sum += t;
        else
        {
            d = x[ind] - x[i];
            sum += maxim(t - 2 * d, 0) + 2 * d;
            i = ind;
        }
        if (i == ind && i == n- 1)sum +=t;
    }
    fout << sum;
    return 0;
}

