const int N = 1e5 + 7;
#define mid ((s + e) / 2)
#define int long long

int n, ed, tad;
int x[N];
int dp[N];
int seg[N * 4];
int laz[N * 4];
int get(int l, int r, int v = 1, int s = 0, int e = N) {
	if (r <= s || e <= l)
		return 2e18;
	if (l <= s && e <= r)
		return seg[v];
	seg[v * 2] += laz[v];
	seg[v * 2 + 1] += laz[v];
	laz[v] = 0;
	return min(get(l, r, v * 2, s, mid), get(l, r, v * 2 + 1, mid, e));
}
void add(int l, int r, int val, int v = 1, int s = 0, int e = N) {
/*	if (v == 1)
		cout << "			add : " << l << ' ' << r << ' ' << val << endl;*/
	if (r <= s || e <= l)
		return;
	if (l <= s && e <= r) {
		seg[v] += val;
		laz[v] += val;
		return;
	}
	seg[v * 2] += laz[v];
	seg[v * 2 + 1] += laz[v];
	laz[v * 2] += laz[v];
	laz[v * 2 + 1] += laz[v];
	laz[v] = 0;
	add(l, r, val, v * 2, s, mid);
	add(l, r, val, v * 2 + 1, mid, e);
	seg[v] = min(seg[v * 2], seg[v * 2 + 1]);
}

