llong seg1[1 << 18];
llong seg2[1 << 18];

void update(llong seg[], int i, int s, int e, int x, llong v) {
    if (s == e) {
        seg[i] = v;
        return;
    }
    int m = (s + e) / 2;
    if (x <= m) update(seg, i << 1, s, m, x, v);
    else update(seg, i << 1 | 1, m + 1, e, x, v);
    seg[i] = min(seg[i << 1], seg[i << 1 | 1]);
}

llong query(llong seg[], int i, int s, int e, int x, int y) {
    if (e < x || y < s) return inf;
    if (x <= s && e <= y) return seg[i];
    int m = (s + e) / 2;
    return min(query(seg, i << 1, s, m, x, y)
               , query(seg, i << 1 | 1, m + 1, e, x, y));
}

int main() {
    for (int i = 0; i < (1 << 18); ++i) seg1[i] = seg2[i] = inf;
    scanf("%d%d%d", &n, &e, &t);
    for (int i = 1; i <= n; ++i) {
        scanf("%d", x + i);
    }
    llong dp;
    update(seg1, 1, 0, n, 0, 0);
    update(seg2, 1, 0, n, 0, -2 * x[1]);
    for (int i = 1; i <= n; ++i) {
        int j = lower_bound(x + 1, x + (n + 1), x[i] - t / 2) - x;
        llong dp1 = query(seg1, 1, 0, n, j - 1, i - 1) + t;
        llong dp2 = query(seg2, 1, 0, n, 0, j - 2) + 2 * x[i];
        dp = min(dp1, dp2);
        update(seg1, 1, 0, n, i, dp);
        update(seg2, 1, 0, n, i, dp - 2 * x[i + 1]);
    }

    printf("%lld\n", dp + e);
	return 0;
}

#include <bits/stdc++.h>
using namespace std;

const int N = 1 << 17;
typedef long long ll;

int n;
int t, e;
int x[N];
ll dp[N];

struct SegT {
	ll Min[N << 1], Lazy[N << 1];

	void pushdown(int i) {
		if (!Lazy[i]) return ;
		Min[i] += Lazy[i];
		if (i < N) {
			Lazy[i << 1] += Lazy[i];
			Lazy[i << 1 | 1] += Lazy[i];
		}
		Lazy[i] = 0;
	}

	void upd(int l, int r, int v, int i = 1, int a = 0, int b = N) {
		if (l <= a && b <= r) {Lazy[i] += v; pushdown(i); return ;}
		pushdown(i); if (r <= a || b <= l) return ;
		int m = (a + b) >> 1;
		if (l < m) upd(l, r, v, i << 1, a, m);
		if (m < r) upd(l, r, v, i << 1 | 1, m, b);
		Min[i] = min(Min[i << 1], Min[i << 1 | 1]);
	}

	ll qry(int l, int r, int i = 1, int a = 0, int b = N) {
		pushdown(i); if (l <= a && b <= r) return Min[i];
		if (r <= a || b <= l) return 1e18;
		int m = (a + b) >> 1; ll ans = 1e18;
		if (l < m) ans = min(ans, qry(l, r, i << 1, a, m));
		if (m < r) ans = min(ans, qry(l, r, i << 1 | 1, m, b));
		return ans;
	}
} segt;

int main() {
	cin >> n >> e >> t;
	++ n;
	for (int i = 1; i < n; ++ i) cin >> x[i];
	dp[0] = 0;
	segt.upd(0, 1, dp[0] + t);
	for (int i = 1, j = 0; i < n; ++ i) {
		segt.upd(0, j, (x[i] - x[i - 1]) * 2);
		while (j < i && (x[i] - x[j + 1]) * 2 >= t) {
			segt.upd(j, j + 1, (x[i] - x[j + 1]) * 2 - t);
			++ j;
		}
		dp[i] = segt.qry(0, i);
		segt.upd(i, i + 1, dp[i] + t);
	}
	cout << dp[n - 1] + e << endl;
	return 0;
}

