        dp[i] = (i > 0 ? dp[i - 1] : 0) + t;
        while (2 * (a[i] - a[j + 1]) >= t)
        {
            ss.erase({dp[j + 1], j + 1});
            kek = min(kek, (j ? dp[j] : 0) - 2 * a[j + 1]);
            j++;
        }
        pair<int, int> it = *(ss.begin());
        dp[i] = min(dp[i], min(it.first + t, kek + 2 * a[i]));
        ss.insert({dp[i], i});
        //cout << j << " " << kek << " " << dp[i] << endl;
    }
    cout << dp[n - 1] + e;
}

