#include <bits/stdc++.h>
using namespace std;
using ll = long long;

struct SegtreeRMQ {
    ll n;
    ll inf;
    vector<ll> dat;

    void init(ll n_input, ll inf_input){
        inf = inf_input;
        n = 1;
        while(n < n_input)n <<= 1;
        dat.resize(2*n-1);
        for(int i = 0; i<2*n-1; i++) dat[i] = inf;
    }

    void update(ll k, ll a){
        k += n - 1;
        dat[k] = a;
        while(k > 0){
            k = (k - 1)/2;
            dat[k] = min(dat[2*k+1], dat[2*k+2]);
        }
    }

    ll query(ll a, ll b, ll k, ll l, ll r){
        if(r<=a || b<=l)return inf;
        if(a<=l && r<=b)return dat[k];
        ll vl = query(a, b, 2*k+1, l, (l+r)/2);
        ll vr = query(a, b, 2*k+2, (l+r)/2, r);
        return min(vl, vr);
    }
};

int main()
{
    ll n, e, t;
    cin >> n >> e >> t;
    SegtreeRMQ st1, st2;
    ll INF = 1e18;
    st1.init(n+10, INF);
    st2.init(n+10, INF);
    vector<ll> x(n+10);
    for(int i = 1; i <= n; i++) cin >> x[i];
    int pos = 0;
    x[n+1] = e;
    vector<ll> ans(n+10);
    st1.update(0, 0);
    st2.update(0, -2 * x[1]);
    for(int i = 1; i <= n; i++)
    {
        while(x[i] - x[pos+1] > t / 2) pos++;
        ans[i] = min(x[i] + t + st1.query(pos, i, 0, 0, st1.n), 3*x[i] + st2.query(0, pos, 0, 0, st2.n));
        st1.update(i, ans[i] - x[i]);
        st2.update(i, ans[i] - x[i] - 2 * x[i+1]);
    }
    cout << ans[n] + e - x[n] << endl;
}

