using namespace std;

typedef long long llong;

const int N = 1 << 17;

const llong inf = 1e15;

struct segtree {
    llong T[2 * N];

    segtree() {
        for (int i = 0; i < 2 * N; i++)
            T[i] = inf;
    }

    void put(int i, llong x) {
        int v = i + N;
        T[v] = min(T[v], x);
        while ((v >>= 1) > 0) {
            T[v] = min(T[2 * v], T[2 * v + 1]);
        }
    }
    llong get(int l, int r) {
        l += N;
        r += N;
        llong ans = inf;
        while (l <= r) {
            ans = min(ans, T[l]);
            ans = min(ans, T[r]);
            l = (l + 1) / 2;
            r = (r - 1) / 2;
        }
        return ans;
    }
} T1, T2;

llong D[N];
llong X[N];

int main() {
    int n;
    scanf("%d", &n);
    llong E, T;
    scanf("%lld %lld", &E, &T);
    for (int i = 0; i < n; i++) {
        scanf("%lld", &X[i]);
    }
    X[n] = E;

    D[0] = X[0];
    T1.put(0, D[0] - X[0]);
    T2.put(0, D[0] - 3 * X[0]);

    int pt = 0;

    for (int i = 1; i <= n; i++) {
        while (2 * (X[i - 1] - X[pt]) > T)
            pt++;
        llong A = T1.get(pt, i - 1) + X[i] + T;
        llong B = T2.get(0, pt - 1) + 2 * X[i - 1] + X[i];
        llong cur = min(A, B);
        D[i] = cur;
        T1.put(i, D[i] - X[i]);
        T2.put(i, D[i] - 3 * X[i]);
    }
    llong ans = D[n];
    printf("%lld\n", ans);

}

