using namespace std;
typedef long long LL;
const int MAXN = 100005;

template <typename T> inline void read(T &AKNOI) {
	T x = 0, flag = 1;
	char ch = getchar();
	while (!isdigit(ch)) {
		if (ch == '-') flag = -1;
		ch = getchar();
	}
	while (isdigit(ch)) {
		x = x * 10 + ch - '0';
		ch = getchar();
	}
	AKNOI = flag * x;
}

int n;
LL E, T, p[MAXN], dp[MAXN];

void init() {
    read(n);
    read(E); read(T);
    for (int i = 1; i <= n; ++i) {
        read(p[i]);
    }
}

void solve() {
    LL mn = 1e18;
    for (int i = 1, j = 0; i <= n; ++i) {
        while (T <= (p[i] - p[j + 1]) * 2) {
            mn = min(mn, dp[j] - p[j + 1] * 2);
            ++j;
        }
        if (j < i) {
            dp[i] = min(mn + p[i] * 2, dp[j] + T);
        } else {
            dp[i] = mn + p[i] * 2;
        }
    }
    printf("%lld\n", dp[n] + E);
}

int main() {
	init();
	solve();
	return 0;
}

