struct RMQ{
  int n;
  vl dat;
  RMQ(int _n){
    n=1;
    while(n<_n)n<<=1;
    dat.assign(2*n,INF);
  }
  void set(int x,int v){
    x += n-1;
    dat[x] = v;
    while(x!=0){
      x = (x-1)/2;
      dat[x] = min(dat[2*x+1],dat[2*x+2]);
    }
  }
  ll qmin(int l,int r,int a,int b,int k){
    if(r<=a || b<=l)return INF;
    if(l<=a && b<=r){
      return dat[k];
    }else{
      int m = (a+b)/2;
      return min(qmin(l,r,a,m,2*k+1),qmin(l,r,m,b,2*k+2));
    }
  }
  ll qmin(int l,int r){
    return qmin(l,r,0,n,0);
  }
};

int main(){
  scanf("%d%d%lld",&n,&e,&t);
  REP(i,n)scanf("%d",x+i);
  x[n] = e;
