  dp[n] = 0;
  // sum of len
  ll ans = x[0];
  REP(i,n)ans += x[i+1]-x[i];
  RMQ aa(n),bb(n);
  // calc dp[i] from n-1 to 0
  FORR(i,0,n){
    aa.set(i,2ll*x[i]+dp[i+1]);
    bb.set(i,t       +dp[i+1]);
    // find m s.t. 2dij<t
    int l=i,r=n+1;
    while(l+1<r){
      int m = (l+r)/2;
      int val = 2*(x[m]-x[i]);
      if(val < t){
        l = m;
      }else{
        r = m;
      }
    }
    ll xx = aa.qmin(max(l+1+5,0),n) - 2ll*x[i];
    ll yy = bb.qmin(0,min(l+1-5,n));
    dp[i] = min(xx,yy);
    FOR(j,min(l+1-5,n),max(l+1+5,0)){
      if(i<=j && j<n)CHMIN(dp[i],calc(i,j));
    }
  }
  printf("%lld\n",ans+dp[0]);
  return 0;
}

