const int N = 1 << 18, B = 400, oo = 1e18 + 100, M = 2520;


void smain() {
    int n;
    cin >> n;

    int t, e;
    cin >> e >> t;

    n++;
    vi kek(n);

    for (int i = 0; i + 1 < n; ++i)
        cin >> kek[i];

    kek.back() = e;
    vi dp(n, oo);
    dp[0] = kek[0];

    for (int i = 0; i + 1 < n; ++i)
    {
        int l = i, r = n - 1;
        while (r - l > 1)
        {
            int m = l + r >> 1;
            if (2 * (kek[m] - kek[i]) < t)
                l = m;
            else
                r = m;
        }

        if (l != i)
            dp[l + 1] = min(dp[l + 1], dp[i] + t + (kek[l + 1] - kek[i]));
        else
            dp[i + 1] = min(dp[i + 1], dp[i] + t + kek[i + 1] - kek[i]);

        if (r != n - 1)
            dp[r + 1] = min(dp[r + 1], dp[i] + 2 * (kek[r] - kek[i]) + (kek[r + 1] - kek[i]));
        else
            dp.back() = min(dp.back(), dp[i] + max(t, 2 * (kek[n - 2] - kek[i])) + (kek[n - 1] - kek[i]));
    }

    cout << dp.back() + e - kek.back();
}

