struct RMQ {
    int N;
    vector<long long> seg;

    RMQ(int n) {
        N = 1 << (int)log2(n * 2 - 1);
        seg.resize(N * 2, 1e18);
    }

    void update(int k, long long v) {
        for (seg[k += N] = v; k > 1; k >>= 1) {
            seg[k >> 1] = min(seg[k], seg[k ^ 1]);
        }
    }

    long long minimum(int l, int r) {
        long long res = 1e18;
        for (l += N, r += N; l < r; l >>= 1, r >>= 1) {
            if (l & 1) res = min(res, seg[l++]);
            if (r & 1) res = min(res, seg[--r]);
        }
        return res;
    }
};

