    for (int i = 0; i < n; i++) {
        // T >= 2 * (ds[i] - ds[j])
        int ok = i + 1;
        int ng = -1;
        while (ok - ng > 1) {
            int mid = (ok + ng) / 2;
            if (T >= 2 * (ds[i] - ds[mid])) ok = mid;
            else ng = mid;
        }
        chmin(dp[i + 1], L.minimum(ok, i + 1));
        chmin(dp[i + 1], R.minimum(0, ok) + 2 * ds[i]);

        L.update(i + 1, dp[i + 1] + T);
        R.update(i + 1, dp[i + 1] - 2 * ds[i]);
    }

    cout << dp[n] + E << endl;
}

