void cng(int seg,int ls,int rs,int ind,int x)
{
  if(ind+1<=ls or rs<=ind)
    return;
  if(ls==rs-1)
    {
      mn[seg]=x;
      return;
    }
  int mid=(ls+rs)>>1;
  cng(lc,ls,mid,ind,x);
  cng(rc,mid,rs,ind,x);
  mn[seg]=min(mn[lc],mn[rc]);
}

int mnr(int seg,int ls,int rs,int l,int r)
{
  if(r<=ls or rs<=l or r<=l)
    return inf;
  if(l<=ls and rs<=r)
    return mn[seg];
  int mid=(ls+rs)>>1;
  return min(mnr(lc,ls,mid,l,r),
	     mnr(rc,mid,rs,l,r));
}

